mapper.update:
update tbl_reply
   set content = #{content}
 where rno = #{rno}

mapper.select:

	 select  
	 	x.*
	 from (
	 	select rownum as rn, t.*
	 	from(
	 		select /*+INDEX(tbl_reply idx_reply) */ 
	      	rno, bno, content, replyDate, email
	    	from tbl_reply
	    	where bno =  #{bno}
	    	and rno > 0
	    	order by replyDate asc
	 	) t
	     
	   
	    where rownum <= #{cri.pageNum} * #{cri.amount}
	    ) x
			where x.rn > (#{cri.pageNum} -1) * #{cri.amount}
